import React, {Component} from 'react';
import './item-add-form.css'
export default class ItemAddForm extends Component {

    state = {
        value: ''
    }

    setValue = (e) => {
        const target = e.target.value;
        target.length === 0 ? false : this.setState({value: e.target.value});
    }

    render() {
        return(
            <div className='item-add-form'>
                <input onChange={this.setValue} value={this.state.value}/>
                <button onClick={() => this.props.addItem(this.state.value)} className="btn btn-oulline-secondary">Add Item</button>
            </div>
        )
    }
};
